  LogOut,
  Menu,
  X
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { cn } from '@/lib/utils';
import { useState } from 'react';

interface SidebarProps {
  onLogout: () => void;
  username?: string;
}

const menuItems = [
  { icon: LayoutDashboard, label: 'Ù„ÙˆØ­Ø© Ø§Ù„ØªØ­ÙƒÙ…', path: '/admin/dashboard', testId: 'nav-dashboard' },
  { icon: Package, label: 'Ø§Ù„Ù…Ù†ØªØ¬Ø§Øª', path: '/admin/products', testId: 'nav-products' },
  { icon: FolderTree, label: 'Ø§Ù„Ø£Ù‚Ø³Ø§Ù…', path: '/admin/categories', testId: 'nav-categories' },
  { icon: ShoppingCart, label: 'Ø§Ù„Ø·Ù„Ø¨Ø§Øª', path: '/admin/orders', testId: 'nav-orders' },
  { icon: Settings, label: 'Ø§Ù„Ø¥Ø¹Ø¯Ø§Ø¯Ø§Øª', path: '/admin/settings', testId: 'nav-settings' },
];

export function Sidebar({ onLogout, username }: SidebarProps) {
  const [location] = useLocation();
  const [mobileOpen, setMobileOpen] = useState(false);

  const SidebarContent = () => (
    <div className="flex flex-col h-full admin-sidebar-bg border-r" style={{ borderColor: 'var(--admin-border)' }}>
      {/* Header */}
      <div className="admin-spacing-md border-b admin-animate-fade" style={{ borderColor: 'var(--admin-border)' }}>
        <div className="admin-header-gradient text-center">
          <h1 className="text-2xl font-bold">
