        </p>
      </div>

      {/* Navigation */}
      <nav className="flex-1 admin-spacing-md space-y-3 admin-animate-slide">
        {menuItems.map((item, index) => {
          const Icon = item.icon;
          const isActive = location === item.path;

          return (
