import { Product } from "@/components/ProductCard";

export type SmartCategory = 
  | "all"
  | "pc_components"
  | "gaming_peripherals"
  | "setup_equipment"
  | "accessories";

export const categoryLabels: Record<SmartCategory, { ar: string; en: string }> = {
  all: { ar: "Ø§Ù„ÙƒÙ„", en: "All" },
  pc_components: { ar: "Ù‚Ø·Ø¹ Ø§Ù„ÙƒÙ…Ø¨ÙŠÙˆØªØ±", en: "PC Components" },
  gaming_peripherals: { ar: "Ù…Ù„Ø­Ù‚Ø§Øª Ø§Ù„Ù‚ÙŠÙ…Ù†Ø¬", en: "Gaming Peripherals" },
  setup_equipment: { ar: "Ø§Ù„Ø¥Ø¹Ø¯Ø§Ø¯ ÙˆØ§Ù„ØªØ¬Ù‡ÙŠØ²Ø§Øª", en: "Setup & Equipment" },
  accessories: { ar: "Ø§ÙƒØ³Ø³ÙˆØ§Ø±Ø§Øª", en: "Accessories" },
};

export function categorizeProduct(product: Product): SmartCategory {
  const name = product.nameEn?.toLowerCase() || product.name.toLowerCase();

  // PC Components: Processors, GPUs, Motherboards, Storage, Cooling, PSU, Cases
  if (
    name.includes('processor') || name.includes('cpu') || 
    name.includes('ryzen') || name.includes('intel') || name.includes('Ù…Ø¹Ø§Ù„Ø¬') ||
    name.includes('graphic') || name.includes('gpu') || 
    name.includes('rtx') || name.includes('gtx') || name.includes('ÙƒØ±Øª Ø´Ø§Ø´Ø©') ||
    name.includes('motherboard') || name.includes('Ù„ÙˆØ­Ø© Ø£Ù…') ||
    name.includes('ssd') || name.includes('hdd') || 
    name.includes('nvme') || name.includes('storage') || name.includes('ØªØ®Ø²ÙŠÙ†') ||
    name.includes('cooling') || name.includes('cooler') || 
    name.includes('fan') || name.includes('ØªØ¨Ø±ÙŠØ¯') ||
    name.includes('power supply') || name.includes('psu') || name.includes('Ø¨Ø§ÙˆØ±') ||
    name.includes('case') || name.includes('ÙƒÙŠØ³')
  ) {
    return 'pc_components';
  }

  // Gaming Peripherals: Keyboards, Mice, Headsets
  if (
    name.includes('keyboard') || name.includes('Ù„ÙˆØ­Ø© Ù…ÙØ§ØªÙŠØ­') ||
    name.includes('mouse') || name.includes('ÙØ£Ø±Ø©') || name.includes('Ù…Ø§ÙˆØ³') ||
    name.includes('headset') || name.includes('headphone') || name.includes('Ø³Ù…Ø§Ø¹Ø©')
  ) {
    return 'gaming_peripherals';
  }

  // Setup & Equipment: Chairs, Monitors, Webcams
  if (
    name.includes('chair') || name.includes('ÙƒØ±Ø³ÙŠ') ||
    name.includes('monitor') || name.includes('display') || name.includes('Ø´Ø§Ø´Ø©') ||
    name.includes('webcam') || name.includes('camera') || name.includes('ÙƒØ§Ù…ÙŠØ±Ø§') ||
    name.includes('mic') || name.includes('microphone') || name.includes('Ù…Ø§ÙŠÙƒ')
  ) {
    return 'setup_equipment';
  }

  // Everything else: Accessories
  return 'accessories';
}

export function filterBySmartCategory(
  products: Product[],
  category: SmartCategory
): Product[] {
  if (category === 'all') {
    return products;
  }

  return products.filter(product => categorizeProduct(product) === category);
}

