    if (combined.match(/Ù„ÙˆØ­Ø© Ø£Ù…|motherboard|mainboard/)) {
      return { categoryId: 'pc-components', subcategoryId: 'motherboards' };
    }

    // PC Components - Ø§Ù„Ù…Ø¹Ø§Ù„Ø¬Ø§Øª
    if (combined.match(/Ù…Ø¹Ø§Ù„Ø¬|processor|cpu|ryzen|i[3579]|ryzen [3579]/) && !combined.match(/motherboard|Ù„ÙˆØ­Ø© Ø£Ù…/)) {
      return { categoryId: 'pc-components', subcategoryId: 'processors' };
    }

    // PC Components - ÙƒØ±ÙˆØª Ø§Ù„Ø´Ø§Ø´Ø©
    if (combined.match(/ÙƒØ±Øª Ø´Ø§Ø´Ø©|graphics|gpu|rtx|gtx|vga|rx [4567]|nvidia|radeon/) && !combined.match(/\bØ´Ø§Ø´Ø©\b|\bmonitor\b/)) {
      return { categoryId: 'pc-components', subcategoryId: 'graphics-cards' };
    }

    // PC Components - Ø§Ù„Ø±Ø§Ù…Ø§Øª
    if (combined.match(/Ø±Ø§Ù…|Ø°Ø§ÙƒØ±Ø©|ram|ddr[345]|memory|16gb|32gb|8gb/) && !combined.match(/ssd|nvme|hdd|storage/)) {
      return { categoryId: 'pc-components', subcategoryId: 'memory' };
    }

    // PC Components - Ø§Ù„ØªØ®Ø²ÙŠÙ†
    if (combined.match(/ØªØ®Ø²ÙŠÙ†|ssd|nvme|hdd|m\.2|storage|hard disk|500gb|1tb|2tb/)) {
      return { categoryId: 'pc-components', subcategoryId: 'storage' };
    }

    // PC Components - Ù…Ø²ÙˆØ¯Ø§Øª Ø§Ù„Ø·Ø§Ù‚Ø©
    if (combined.match(/Ù…Ø²ÙˆØ¯ Ø·Ø§Ù‚Ø©|power supply|psu|watt|bronze|gold|platinum|[567][05]0w/)) {
      return { categoryId: 'pc-components', subcategoryId: 'power-supply' };
    }

    // PC Components - ÙƒÙŠØ³ Ø§Ù„ÙƒÙ…Ø¨ÙŠÙˆØªØ±
    if (combined.match(/ÙƒÙŠØ³|case|chassis|tower/)) {
      return { categoryId: 'pc-components', subcategoryId: 'cases' };
    }

    // PC Components - Ø§Ù„Ù…Ø¨Ø±Ø¯Ø§Øª (cooling fans)
    if (combined.match(/Ù…Ø¨Ø±Ø¯|ØªØ¨Ø±ÙŠØ¯|Ù…Ø±ÙˆØ­Ø©|Ù…Ø±Ø§ÙˆØ­|cooler|cooling|fan|liquid|airflow|aio/) && !combined.match(/ÙƒÙŠØ³|case/)) {
      return { categoryId: 'pc-components', subcategoryId: 'cooling' };
    }

    // Peripherals - Ù„ÙˆØ­Ø§Øª Ø§Ù„Ù…ÙØ§ØªÙŠØ­
    if (combined.match(/ÙƒÙŠØ¨ÙˆØ±Ø¯|Ù„ÙˆØ­Ø© Ù…ÙØ§ØªÙŠØ­|keyboard|mechanical|rgb keyboard|keychron/)) {
      return { categoryId: 'peripherals', subcategoryId: 'keyboards' };
    }

    // Peripherals - Ø§Ù„Ù…Ø§ÙˆØ³
    if (combined.match(/Ù…Ø§ÙˆØ³(?! Ø¨Ø§Ø¯)|mouse(?! pad)|dpi|wireless mouse|gaming mouse/) && !combined.includes('pad')) {
      return { categoryId: 'peripherals', subcategoryId: 'mice' };
    }

    // Peripherals - Ø§Ù„Ø³Ù…Ø§Ø¹Ø§Øª
    if (combined.match(/Ø³Ù…Ø§Ø¹Ø©|Ø³Ù…Ø§Ø¹Ø§Øª|headset|headphone|hyperx|razer|gaming headset/)) {
      return { categoryId: 'peripherals', subcategoryId: 'headsets' };
    }

    // Peripherals - Mouse Pads
    if (combined.match(/Ù…Ø§ÙˆØ³ Ø¨Ø§Ø¯|mouse ?pad|mousepad/)) {
      return { categoryId: 'peripherals', subcategoryId: 'mouse-pads' };
    }

    // Streaming Gear - Ø§Ù„ÙƒØ§Ù…ÙŠØ±Ø§Øª
    if (combined.match(/ÙƒØ§Ù…ÙŠØ±Ø§|webcam|camera|streaming cam|logitech.*c[97]/)) {
      return { categoryId: 'streaming-gear', subcategoryId: 'cameras' };
    }

    // Streaming Gear - Ø§Ù„Ù…Ø§ÙŠÙƒØ±ÙˆÙÙˆÙ†Ø§Øª
    if (combined.match(/Ù…Ø§ÙŠÙƒ|Ù…ÙŠÙƒØ±ÙˆÙÙˆÙ†|microphone|mic|condenser|blue yeti|usb mic/)) {
      return { categoryId: 'streaming-gear', subcategoryId: 'microphones' };
    }

    // Setup Accessories - Ø§Ù„ÙƒØ±Ø§Ø³ÙŠ (moved from peripherals)
    if (combined.match(/ÙƒØ±Ø³ÙŠ|chair|gaming chair|ergonomic|Ù…Ù‚Ø¹Ø¯/)) {
      return { categoryId: 'setup-accessories', subcategoryId: 'chairs' };
    }

    // Setup Accessories - Ø§Ù„Ø¥Ø¶Ø§Ø¡Ø© (moved from streaming-gear)
    if (combined.match(/led|rgb|Ø¥Ø¶Ø§Ø¡Ø©|light bar|strip|govee|Ù†ÙŠÙˆÙ†/)) {
      return { categoryId: 'setup-accessories', subcategoryId: 'lighting' };
    }

    // Setup Accessories - Controllers (moved from ready-builds)
    if (combined.match(/ØªØ­ÙƒÙ…|controller|gamepad|joystick|xbox|ps[45]/)) {
      return { categoryId: 'setup-accessories', subcategoryId: 'controllers' };
    }

    // Setup Accessories - Stands
    if (combined.match(/Ø­Ø§Ù…Ù„|stand|mount|arm|Ù‚Ø§Ø¹Ø¯Ø©/)) {
      return { categoryId: 'setup-accessories', subcategoryId: 'stands' };
    }

    // Setup Accessories - Adapters (renamed from hubs-adapters)
    if (combined.match(/hub|adapter|Ù…Ø­ÙˆÙ„|bluetooth|wi-?fi|usb.*hub|Ø´Ø¨ÙƒØ©|network/)) {
      return { categoryId: 'setup-accessories', subcategoryId: 'adapters' };
    }

    // Setup Accessories - Smart Accessories
    if (combined.match(/sensor|Ù…Ø³ØªØ´Ø¹Ø±|smart|Ø°ÙƒÙŠ|iot/)) {
      return { categoryId: 'setup-accessories', subcategoryId: 'smart-accessories' };
    }

    // Ready Builds - Ø§Ù„ØªØ¬Ù…ÙŠØ¹Ø§Øª (only PC builds now)
    if (combined.match(/ØªØ¬Ù…ÙŠØ¹Ø©|pc build|bundle|pre.?built/)) {
      return { categoryId: 'ready-builds', subcategoryId: 'pc-builds' };
    }

    // Fallback to generic PC components
    return { categoryId: 'pc-components', subcategoryId: 'processors' };
  }

  // Get categories with subcategories from database (public endpoint)
  app.get("/api/categories", async (req, res) => {
    try {
      // Fetch categories and subcategories using Drizzle ORM
      const [categoriesData, subcategoriesData] = await Promise.all([
        db.select().from(categories).where(eq(categories.isActive, true)).orderBy(categories.sortOrder),
        db.select().from(subcategories).where(eq(subcategories.isActive, true)).orderBy(subcategories.sortOrder)
      ]);

      // Map categories with their subcategories
      const categoriesWithSubcategories = categoriesData.map((category) => {
        const categorySubcategories = subcategoriesData
          .filter((sub) => sub.categoryId === category.id)
          .map((sub) => ({
            id: sub.id,
            name: sub.name,
            nameEn: sub.nameEn,
            icon: sub.icon,
            description: sub.description,
            descriptionEn: sub.descriptionEn
          }));

        return {
          id: category.id,
          name: category.name,
          nameEn: category.nameEn,
          icon: category.icon,
          description: category.description,
          descriptionEn: category.descriptionEn,
          color: category.color,
          gradient: category.gradient,
          subcategories: categorySubcategories
        };
      });

      console.log(`âœ… API: Returning ${categoriesWithSubcategories.length} categories with subcategories`);
      res.json(categoriesWithSubcategories);
    } catch (error) {
      console.error('Error fetching categories:', error);
      res.status(500).json({ error: 'Failed to fetch categories' });
    }
  });

  // Get exchange rate setting
  app.get("/api/settings/:key", async (req, res) => {
    try {
      const { key } = req.params;
      const [setting] = await db.select().from(settings).where(eq(settings.key, key));

      if (!setting) {
        return res.status(404).json({ error: 'Setting not found' });
      }

      // Return the setting with proper JSONB structure
      res.json({
        key: setting.key,
        value: setting.value,
        rate: (setting.value as { rate: number }).rate
      });
    } catch (error) {
      console.error('Error fetching setting:', error);
      res.status(500).json({ error: 'Failed to fetch setting' });
    }
  });

  // ============= ADMIN API ROUTES =============

  // Admin: Get all products (including inactive) from Supabase
  app.get("/api/admin/products", async (req, res) => {
    try {
      const { data: allProducts, error: productsError } = await adminSupabase
        .from('products')
        .select('*')
        .order('created_at', { ascending: false });

      if (productsError) throw productsError;

      const { data: settingsData, error: settingsError } = await adminSupabase
        .from('settings')
        .select('*')
        .eq('key', 'usd_to_lyd_rate')
        .single();

      const exchangeRate = settingsData ? (settingsData.value as { rate: number }).rate : 5.10;

      const productsWithPricing = (allProducts || []).map((product: any) => ({
        id: product.id,
        name: product.name,
        nameEn: product.name_en,
        description: product.description,
        descriptionEn: product.description_en,
        basePriceUsd: parseFloat(String(product.base_price_usd)) || 0,
        categoryId: product.category_id,
        subcategoryId: product.subcategory_id,
        category: product.category,
        image: product.image,
        inStock: product.in_stock,
        stockCount: product.stock_count,
        isActive: product.is_active,
        displayPriceLyd: roundLYD((parseFloat(String(product.base_price_usd)) || 0) * exchangeRate),
        usdToLydRate: exchangeRate,
        createdAt: product.created_at,
        updatedAt: product.updated_at
      }));

      res.json(productsWithPricing);
    } catch (error) {
      console.error('Error fetching admin products:', error);
      res.status(500).json({ error: 'Failed to fetch products' });
    }
  });

  // Admin: Create product in Supabase database
  app.post("/api/admin/products", async (req, res) => {
    try {
      const productData = req.body;

      const { data: newProduct, error } = await adminSupabase
        .from('products')
        .insert({
          id: productData.id,
          name: productData.name,
          name_en: productData.nameEn,
          description: productData.description,
          description_en: productData.descriptionEn,
          base_price_usd: String(productData.basePriceUsd),
          category: productData.category || '',
          category_id: productData.categoryId,
          subcategory_id: productData.subcategoryId,
          image: productData.image,
          in_stock: productData.inStock !== false,
          stock_count: productData.stockCount || 0,
          is_active: productData.isActive !== false
        })
        .select()
        .single();

      if (error) {
        console.error('Supabase insert error:', error);
        throw error;
      }

      res.status(201).json(newProduct);
    } catch (error) {
      console.error('Error creating product:', error);
      res.status(500).json({ error: 'Failed to create product' });
    }
  });

  // Admin: Update product in Supabase database
  app.put("/api/admin/products/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const productData = req.body;

      const { data: updated, error } = await adminSupabase
        .from('products')
        .update({
          name: productData.name,
          name_en: productData.nameEn,
          description: productData.description,
          description_en: productData.descriptionEn,
          base_price_usd: String(productData.basePriceUsd),
          category: productData.category || '',
          category_id: productData.categoryId,
          subcategory_id: productData.subcategoryId,
          image: productData.image,
          in_stock: productData.inStock,
          stock_count: productData.stockCount,
          is_active: productData.isActive,
          updated_at: new Date().toISOString()
        })
        .eq('id', id)
        .select()
        .single();

      if (error) {
        console.error('Supabase update error:', error);
        if (error.code === 'PGRST116') {
          return res.status(404).json({ error: 'Product not found' });
        }
        throw error;
      }

      res.json(updated);
    } catch (error) {
      console.error('Error updating product:', error);
      res.status(500).json({ error: 'Failed to update product' });
    }
  });

  // Admin: Delete product from Supabase database
  app.delete("/api/admin/products/:id", async (req, res) => {
