server/api/orders.ts.content:
import { Request, Response } from 'express';

interface OrderData {
  product: {
    id: string;
    name: string;
    price: number;
  };
  customer: {
    name: string;
    phone: string;
    city: string;
    address: string;
    notes?: string;
  };
}

// In-memory storage for orders (in production, use a database)
const orders: Array<OrderData & { id: string; timestamp: Date; status: string }> = [];

export const createOrder = async (req: Request, res: Response) => {
  try {
    const orderData: OrderData = req.body;

    // Validate required fields
    if (!orderData.product?.id || !orderData.customer?.name || !orderData.customer?.phone) {
      return res.status(400).json({ 
        error: 'Missing required fields' 
      });
    }

    // Create order with ID and timestamp
    const order = {
      ...orderData,
      id: `order_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      timestamp: new Date(),
      status: 'pending'
    };

    // Store order
    orders.push(order);

    // Log order for debugging
