  animation-delay: 0.2s;
  animation-fill-mode: both;
}

.animation-delay-400 {
  animation-delay: 0.4s;
  animation-fill-mode: both;
}

.drop-shadow-glow {
  filter: drop-shadow(0 0 10px rgba(250, 255, 0, 0.5));
}

