.gaming-button {
  background: linear-gradient(135deg, #FAFF00, #F5FF42);
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.gaming-button:hover {
  background: linear-gradient(135deg, #F5FF42, #FAFF00);
  transform: translateY(-2px);
  box-shadow: 0 20px 40px rgba(250, 255, 0, 0.3);
}

