  from {
    opacity: 0;
    transform: translateX(20px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes adminFadeIn {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.admin-animate-slide {
  animation: adminSlideIn 0.4s ease-out;
}

.admin-animate-fade {
  animation: adminFadeIn 0.5s ease-out;
}

/* Admin Badge Styles */
.admin-badge {
  padding: 6px 12px;
  border-radius: 8px;
  font-size: 12px;
  font-weight: 600;
  display: inline-block;
}

.admin-badge-success {
